import React, { useState } from 'react';
import { View, TextInput, StyleSheet, Text, Alert, TouchableOpacity } from 'react-native';
import { Button } from 'react-native-elements'
import Axios from 'axios';


function Login( {setIsLoggedIn, setUser, user, navigation} ) {

  const [ password, setPassword ] = useState('')


const onLogin = () => {
  Axios.get(`https://us-central1-trash-2b5de.cloudfunctions.net/app/api/users/${user}`)
  .then(res => {
    if (res.data[0].password === password) {
      setIsLoggedIn(true)
      } else {
        Alert.alert(
          'Wrong Entry',
          'Password or Username Invalid',
          [
            {
              text: 'Ok',
            }
          ]
        )
      }
    }
  )
} 






  return (
    <View style={styles.container}>
        <Text style={styles.header}>#Trash Tagger</Text>
        <TextInput
          value={user}
          onChangeText={(username)=> setUser(username)}
          placeholder={'Username'}
          style={styles.input} />
        <TextInput
          value={password}
          onChangeText={(password) => setPassword(password)}
          placeholder={'Password'}
          secureTextEntry={true}
          style={styles.input}
          />

          <TouchableOpacity 
            style={styles.button}
            onPress={onLogin}  
          >
            <Text style={styles.backbtn}>Login</Text> 
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('RegisterUser')}
          >
            <Text style={styles.backbtn}>Register</Text> 
          </TouchableOpacity>
  </View>
  )
}
const styles = StyleSheet.create({
  button: {
    elevation: 8,
    borderRadius: 25,
    paddingVertical: 10,
    paddingHorizontal: 12,
    backgroundColor: '#D65F56',
    height: 45,
    width: '80%',
    marginTop: 20
},
  backbtn: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
    alignSelf: 'center',
    textTransform: 'uppercase',

},
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ecf0f1',
  },
  input: {
    width: '80%',
    height: 44,
    padding: 10,
   borderBottomWidth: 0.5,
    borderBottomColor: 'gray',
    marginBottom: 10,

  },
  header: {
    fontSize: 40,
    fontWeight: "bold",
    marginBottom: 100

  }
  });
export default Login
